func TestSize(t *testing.T) {
	tests := []struct {
		name    string
		message *hep.Message
		size    int
	}{
		{
			name:    "Empty Message",
			message: &hep.Message{},
			size:    81,
		},
		{
			name: "Message with SrcIP",
			message: &hep.Message{
				SrcIP: net.ParseIP("192.168.0.1").To4(),
			},
			size: 91,
		},
		{
			name: "Message with DstIP",
			message: &hep.Message{
				DstIP: net.ParseIP("192.168.0.2").To4(),
			},
			size: 91,
		},
		{
			name: "Message with NodePW",
			message: &hep.Message{
				NodePW: "password",
			},
			size: 95,
		},
		{
			name: "Message with Payload",
			message: &hep.Message{
				Payload: []byte{0x01, 0x02, 0x03},
			},
			size: 90,
		},
		{
			name: "Message with CID",
			message: &hep.Message{
				CID: []byte{0x01, 0x02, 0x03, 0x04},
			},
			size: 91,
		},
		{
			name: "Message with NodeName",
			message: &hep.Message{
				NodeName: "node",
			},
			size: 91,
		},
		{
			name: "Message with SrcIP, DstIP, NodePW, Payload, CID, and NodeName",
			message: &hep.Message{
				SrcIP:    net.ParseIP("192.168.0.1").To4(),
				DstIP:    net.ParseIP("192.168.0.2").To4(),
				NodePW:   "password",
				Payload:  []byte{0x01, 0x02, 0x03},
				CID:      []byte{0x01, 0x02, 0x03, 0x04},
				NodeName: "node",
			},
			size: 144,
		},
	}

	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			size := Size(test.message)
			if size != test.size {
				t.Errorf("Expected size %d, but got %d", test.size, size)
			}
		})
	}
}

func TestEncode(t *testing.T) {
	encoder := NewBinaryEncoder()
	hepMsg := &hep.Message{
		IPProtocolFamily: byte(0x2),
		IPProtocolID:     byte(0x11),
		SrcIP:            net.ParseIP("192.168.247.250").To4(),
		DstIP:            net.ParseIP("192.168.245.250").To4(),
		DstPort:          5060,
		SrcPort:          5060,
		Tsec:             1712018425,
		Tmsec:            119540,
		Payload:          sipPayload,
		Vlan:             0,
		ProtoType:        1,
		NodeID:           101,
		NodePW:           "passwd",
	}

	// Test case 1: Valid HEP packet
	t.Run("Valid HEP packet", func(t *testing.T) {
		hepPacket, err := encoder.Encode(hepMsg)
		assert.NoError(t, err)
		assert.Equal(t, validPacket, hepPacket)
	})
	// Test case 2: Set Timestamp and check if Tsec and Tmsec takes precedence
	t.Run("Tsec and Tmsec precedence", func(t *testing.T) {
		hepMsg.Timestamp = time.Unix(1234, 1234)
		hepPacket, err := encoder.Encode(hepMsg)
		assert.NoError(t, err)
		assert.Equal(t, validPacket, hepPacket)
	})
	// Test case 3: Valid Timestamp conversion
	t.Run("Timestamp conversion", func(t *testing.T) {
		hepMsg.Timestamp = time.Unix(1712018425, 119540000)
		hepMsg.Tsec = 0
		hepMsg.Tmsec = 0
		hepPacket, err := encoder.Encode(hepMsg)
		assert.NoError(t, err)
		assert.Equal(t, validPacket, hepPacket)
	})
}

func BenchmarkEncode(b *testing.B) {
	encoder := NewBinaryEncoder()
	for i := 0; i < b.N; i++ {
		hepMsg := &hep.Message{
			IPProtocolFamily: byte(0x2),
			IPProtocolID:     byte(0x11),
			SrcIP:            net.ParseIP("192.168.247.250").To4(),
			DstIP:            net.ParseIP("192.168.245.250").To4(),
			DstPort:          5060,
			SrcPort:          5060,
			Tsec:             1712018425,
			Tmsec:            119540,
			Payload:          sipPayload,
			Vlan:             0,
			ProtoType:        1,
			NodeID:           101,
			NodePW:           "passwd",
		}
		encoder.Encode(hepMsg)
	}
}
